import { Component, Input, ViewEncapsulation } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-shiny-button',
  standalone: true,
  imports: [CommonModule],
  encapsulation: ViewEncapsulation.None,
  template: `
    <button [class]="'shiny-cta ' + className">
      <div class="gradient-border"></div>

      <div class="inner-bg"></div>

      <span class="content"><ng-content></ng-content></span>
    </button>
  `,
  styles: [`
    /* Визначаємо змінні (Houdini API) для плавності */
    @property --gradient-angle {
      syntax: "<angle>";
      initial-value: 0deg;
      inherits: false;
    }

    .shiny-cta {
      --shiny-cta-bg: #2E1A12;          /* Твій темний фон */
      --shiny-cta-highlight: hsl(24 66% 53%); /* Помаранчевий */
      --shiny-cta-highlight-subtle: hsl(45 86% 62%); /* Жовтий */

      position: relative;
      isolation: isolate;
      overflow: hidden;
      border: none;
      outline: none;
      background: transparent;
      padding: 1rem 2.5rem;
      border-radius: 18px; /* Головний радіус */
      cursor: pointer;
      font-family: inherit;
      font-size: 1rem;
      font-weight: 600;
      color: white;

      /* Анімація обертання */
      animation: rotate-gradient 3s linear infinite;
    }

    /* 1. Шар градієнта (Рамка) */
    .gradient-border {
      position: absolute;
      inset: -50%; /* Робимо його значно більшим за кнопку */
      background: conic-gradient(
        from var(--gradient-angle),
        transparent,
        var(--shiny-cta-highlight),
        var(--shiny-cta-highlight-subtle),
        var(--shiny-cta-highlight),
        transparent
      );
      z-index: 0;
      animation: rotate-gradient 3s linear infinite;
    }

    /* 2. Шар фону (Щоб перекрити центр) */
    .inner-bg {
      position: absolute;
      inset: 2px; /* Відступ 2px створює товщину рамки */
      background: var(--shiny-cta-bg);
      border-radius: 16px; /* Трохи менший радіус (18px - 2px) */
      z-index: 1;
    }

    /* 3. Текст */
    .content {
      position: relative;
      z-index: 2; /* Текст поверх усього */
    }

    /* Анімація обертання */
    @keyframes rotate-gradient {
      0% { --gradient-angle: 0deg; }
      100% { --gradient-angle: 360deg; }
    }

    /* Ефекти при наведенні */
    .shiny-cta:hover .inner-bg {
      background: #3a221a; /* Трохи світліший при наведенні */
    }

    .shiny-cta:active {
      transform: scale(0.98);
    }

    /* Додатковий відблиск зверху */
    .shiny-cta::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 50%;
      background: linear-gradient(to bottom, rgba(255,255,255,0.1), transparent);
      z-index: 2;
      border-radius: 18px 18px 0 0;
      pointer-events: none;
    }
  `]
})
export class ShinyButtonComponent {
  @Input() className: string = '';
}
