import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-project-button',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <a [routerLink]="link" 
       class="group relative inline-flex items-center justify-center overflow-hidden rounded-full border border-brand-orange/30 bg-white px-6 py-3 font-medium text-brand-dark shadow-sm transition-all duration-300 hover:border-brand-orange hover:shadow-md hover:shadow-brand-orange/20 cursor-pointer w-auto">

      <div class="flex items-center gap-3 transition-all duration-300 group-hover:-translate-y-[150%]">
        <div class="h-2 w-2 rounded-full bg-brand-orange"></div>
        <span><ng-content></ng-content></span>
      </div>

      <div class="absolute left-0 top-0 h-2 w-2 rounded-full bg-brand-orange transition-all duration-500 group-hover:scale-[100]"></div>

      <div class="absolute inset-0 z-10 flex items-center justify-center gap-2 text-white opacity-0 transition-all duration-300 group-hover:opacity-100">
        <span class="whitespace-nowrap font-bold"><ng-content></ng-content></span>
        <span class="material-icons-round text-sm">arrow_forward</span>
      </div>
    </a>
  `
})
export class ProjectButtonComponent {
  // Приймаємо посилання як масив (для роутера) або рядок
  @Input() link: any[] | string = [];
}
