import { Component, ChangeDetectionStrategy, signal, inject } from '@angular/core';
import { TranslationService, Language } from '../services/translation.service';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule],
  template: `
    <nav [class]="'fixed top-0 left-0 w-full z-50 transition-all duration-300 ' + (scrolled() ? 'bg-brand-dark/95 shadow-lg py-2' : 'bg-transparent py-4')">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div class="flex justify-between items-center h-16">

          <div class="flex-shrink-0 flex items-center gap-2 cursor-pointer" (click)="scrollTo('home')">
            <div class="w-10 h-10 border-2 border-brand-orange rounded-lg flex items-center justify-center text-brand-orange font-bold text-xl">
              Ц
            </div>
            <div class="flex flex-col">
              <span class="font-bold text-lg leading-none text-white tracking-wide">CELA</span>
              <span class="text-[10px] text-slate-400 uppercase tracking-widest">Analytics Center</span>
            </div>
          </div>

          <div class="hidden md:flex items-center space-x-8">
            <button (click)="scrollTo('about')" class="text-sm font-medium text-slate-300 hover:text-brand-yellow transition-colors uppercase tracking-wide">{{ ts.t().nav.about }}</button>
            <button (click)="scrollTo('services')" class="text-sm font-medium text-slate-300 hover:text-brand-yellow transition-colors uppercase tracking-wide">{{ ts.t().nav.services }}</button>
            <button (click)="scrollTo('projects')" class="text-sm font-medium text-slate-300 hover:text-brand-yellow transition-colors uppercase tracking-wide">{{ ts.t().nav.projects }}</button>
            <button (click)="scrollTo('team')" class="text-sm font-medium text-slate-300 hover:text-brand-yellow transition-colors uppercase tracking-wide">{{ ts.t().nav.team }}</button>
            <button (click)="scrollTo('contact')" class="text-sm font-medium text-slate-300 hover:text-brand-yellow transition-colors uppercase tracking-wide">{{ ts.t().nav.contact }}</button>
          </div>

          <div class="hidden md:flex items-center gap-6">
            <div class="flex items-center text-xs font-bold text-slate-500">
              <button (click)="setLang('uk')" [class]="getLangClass('uk')">UA</button>
              <span class="mx-2">|</span>
              <button (click)="setLang('pl')" [class]="getLangClass('pl')">PL</button>
              <span class="mx-2">|</span>
              <button (click)="setLang('en')" [class]="getLangClass('en')">ENG</button>
            </div>

            <button (click)="openSupport()" class="px-6 py-2 bg-brand-orange text-white text-xs font-bold uppercase tracking-wider rounded-squircle hover:bg-[#C06020] transition-colors shadow-lg shadow-brand-orange/20">
              {{ ts.t().nav.support }}
            </button>
          </div>

          <div class="md:hidden flex items-center gap-4">
             <button (click)="toggleMenu()" class="text-white p-2">
               <span class="material-icons-round text-3xl">{{ isMenuOpen() ? 'close' : 'menu' }}</span>
             </button>
          </div>
        </div>
      </div>

      @if (isMenuOpen()) {
        <div class="md:hidden bg-brand-dark border-t border-white/10 absolute w-full left-0 top-full shadow-2xl">
          <div class="px-4 py-6 space-y-4 flex flex-col items-center">
            <button (click)="mobileScrollTo('about')" class="text-white text-lg font-medium">{{ ts.t().nav.about }}</button>
            <button (click)="mobileScrollTo('services')" class="text-white text-lg font-medium">{{ ts.t().nav.services }}</button>
            <button (click)="mobileScrollTo('contact')" class="text-white text-lg font-medium">{{ ts.t().nav.contact }}</button>
            <button (click)="openSupport()" class="w-full py-3 bg-brand-orange text-white font-bold rounded-lg">{{ ts.t().nav.support }}</button>
          </div>
        </div>
      }
    </nav>
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class NavbarComponent {
  ts = inject(TranslationService);
  isMenuOpen = signal(false);
  scrolled = signal(false);

  constructor() {
    // Додаємо ефект затемнення хедеру при прокрутці
    window.addEventListener('scroll', () => {
      this.scrolled.set(window.scrollY > 50);
    });
  }

  toggleMenu() { this.isMenuOpen.update(v => !v); }

  scrollTo(id: string) {
    const element = document.getElementById(id);
    if (element) element.scrollIntoView({ behavior: 'smooth' });
  }

  mobileScrollTo(id: string) {
    this.scrollTo(id);
    this.isMenuOpen.set(false);
  }

  setLang(lang: Language) { this.ts.setLanguage(lang); }

  getLangClass(lang: Language): string {
    return this.ts.currentLang() === lang ? 'text-white' : 'hover:text-white transition-colors';
  }

  openSupport() { alert('Посилання на донат'); }
}
