    }

    .shiny-cta::before,
    .shiny-cta::after,
    .shiny-cta span::before {
      content: "";
      pointer-events: none;
      position: absolute;
      inset-inline-start: 50%;
      inset-block-start: 50%;
      translate: -50% -50%;
      z-index: -1;
      border-radius: inherit; /* Наслідуємо радіус */
    }

    .shiny-cta:active {
      translate: 0 1px;
    }

    /* Dots pattern */
    .shiny-cta::before {
      --size: calc(100% - var(--shadow-size) * 3);
