import { Component, ChangeDetectionStrategy } from '@angular/core';
import { CommonModule } from '@angular/common';
// ... імпорти компонентів (Hero, Navbar, etc.) залишаються ті самі
import { NavbarComponent } from './components/navbar.component';
import { HeroComponent } from './components/hero.component';
import { AboutComponent } from './components/about.component';
import { ServicesComponent } from './components/services.component';
import { TeamComponent } from './components/team.component';
import { ContactComponent } from './components/contact.component';
import { FooterComponent } from './components/footer.component';
import { ProjectsComponent } from './components/projects.component';
import { PublicationsComponent } from './components/publications.component';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [
    CommonModule,
    NavbarComponent,
    HeroComponent,
    AboutComponent,
    ServicesComponent,
    TeamComponent,
    ContactComponent,
    FooterComponent,
    ProjectsComponent,
    PublicationsComponent
  ],
  // ЗМІНА: bg-[#2E1A12] для всієї сторінки, щоб уникнути білих смуг
  template: `
    <div class="flex flex-col min-h-screen font-sans bg-[#2E1A12] text-white">
      <app-navbar></app-navbar>
      <main class="flex-grow">
        <app-hero></app-hero>
        <app-about></app-about>
        <app-services></app-services>
        <app-projects></app-projects>
        <app-team></app-team>
        <app-publications></app-publications>
        <app-contact></app-contact>
      </main>
      <app-footer></app-footer>
    </div>
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class AppComponent {}
