import { Component, ChangeDetectionStrategy, inject, computed } from '@angular/core';
import { TranslationService } from '../services/translation.service';

@Component({
  selector: 'app-projects',
  standalone: true,
  template: `
    <section id="projects" class="py-20 bg-brand-cream">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div class="text-center mb-16">
          <h2 class="text-3xl font-bold text-brand-dark sm:text-4xl">{{ ts.t().projects.title }}</h2>
          <div class="w-20 h-1 bg-brand-orange mx-auto mt-4 rounded-full"></div>
        </div>

        <div class="grid grid-cols-1 md:grid-cols-2 gap-12">

          <div class="bg-white p-8 rounded-squircle shadow-sm border-t-4 border-brand-orange">
            <div class="flex items-center gap-3 mb-6">
              <span class="material-icons-round text-brand-orange text-3xl">public</span>
              <h3 class="text-2xl font-bold text-brand-dark">{{ ts.t().projects.donors }}</h3>
            </div>
            <div class="space-y-6">
              @for (item of donorProjects(); track item.title) {
                <div class="p-4 bg-slate-50 rounded-lg border border-slate-100 hover:shadow-md transition-shadow">
                  <h4 class="font-bold text-lg mb-2">{{ item.title }}</h4>
                  <p class="text-slate-600 text-sm">{{ item.desc }}</p>
                </div>
              }
            </div>
          </div>

          <div class="bg-white p-8 rounded-squircle shadow-sm border-t-4 border-brand-yellow">
            <div class="flex items-center gap-3 mb-6">
              <span class="material-icons-round text-brand-yellow text-3xl">volunteer_activism</span>
              <h3 class="text-2xl font-bold text-brand-dark">{{ ts.t().projects.probono }}</h3>
            </div>
            <div class="space-y-6">
              @for (item of probonoProjects(); track item.title) {
                <div class="p-4 bg-slate-50 rounded-lg border border-slate-100 hover:shadow-md transition-shadow">
                  <h4 class="font-bold text-lg mb-2">{{ item.title }}</h4>
                  <p class="text-slate-600 text-sm">{{ item.desc }}</p>
                </div>
              }
            </div>
          </div>

        </div>
      </div>
    </section>
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProjectsComponent {
  ts = inject(TranslationService);

  // Фільтруємо проєкти за типом
  donorProjects = computed(() => this.ts.t().projects.items.filter(p => p.type === 'donor'));
  probonoProjects = computed(() => this.ts.t().projects.items.filter(p => p.type === 'probono'));
}
