import { Component, ChangeDetectionStrategy, inject } from '@angular/core';
import { TranslationService } from '../services/translation.service';

@Component({
  selector: 'app-hero',
  standalone: true,
  template: `
    <section id="home" class="relative pt-32 pb-40 md:pt-48 md:pb-56 overflow-hidden bg-brand-dark text-white">

      <div class="absolute inset-0 z-0">
         <img src="https://images.unsplash.com/photo-1497366216548-37526070297c?q=80&w=2069&auto=format&fit=crop" 
              class="w-full h-full object-cover opacity-20 mix-blend-overlay" alt="Background">
         <div class="absolute inset-0 bg-gradient-to-b from-brand-dark/90 via-brand-dark/80 to-brand-cream"></div>
      </div>

      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center relative z-10">
        <div class="inline-flex items-center px-4 py-1.5 rounded-full bg-white/5 border border-white/10 backdrop-blur-md text-brand-yellow text-sm font-medium mb-8">
          <span class="material-icons-round text-base mr-2">verified</span>
          {{ ts.t().hero.badge }}
        </div>

        <h1 class="text-4xl md:text-6xl font-extrabold tracking-tight mb-6 leading-tight">
          {{ ts.t().hero.titleStart }} <br class="hidden md:block"/>
          <span class="text-transparent bg-clip-text bg-gradient-to-r from-brand-yellow to-brand-orange">
            {{ ts.t().hero.titleEnd }}
          </span>
        </h1>

        <p class="mt-4 max-w-2xl mx-auto text-xl text-slate-300 mb-10 font-light leading-relaxed">
          {{ ts.t().hero.description }}
        </p>

        <div class="flex flex-col sm:flex-row justify-center gap-4">
          <button (click)="scrollTo('contact')" class="px-8 py-4 bg-brand-orange text-white font-bold rounded-squircle shadow-xl shadow-brand-orange/20 hover:bg-[#C06020] hover:-translate-y-1 transition-all flex items-center justify-center text-sm uppercase tracking-wider">
            {{ ts.t().hero.btnContact }}
          </button>

          <button (click)="scrollTo('about')" class="px-8 py-4 bg-white/5 text-white font-bold rounded-squircle backdrop-blur-sm border border-white/10 hover:bg-white/10 transition-all flex items-center justify-center text-sm uppercase tracking-wider">
            {{ ts.t().hero.btnMore }}
          </button>
        </div>
      </div>
    </section>
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class HeroComponent {
  ts = inject(TranslationService);
  scrollTo(id: string) { document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' }); }
}
