import { Component, inject, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms'; // <-- 1. Важливий імпорт для форм
import { TranslationService } from '../services/translation.service';
import { AppwriteService } from '../services/appwrite.service'; // Імпорт сервісу

@Component({
  selector: 'app-contact',
  standalone: true,
  imports: [CommonModule, FormsModule], // <-- 2. Додаємо FormsModule сюди
  template: `
    <div class="bg-white p-8 rounded-3xl shadow-lg">
      <h3 class="text-2xl font-bold text-brand-dark mb-2">{{ ts.t().contact.formTitle }}</h3>
      <p class="text-slate-500 mb-6">Заповніть форму, і наші експерти зв'яжуться з вами.</p>

      <form (ngSubmit)="onSubmit()" #contactForm="ngForm">

        <div class="mb-4">
          <input 
            type="text" 
            [(ngModel)]="formData.name" 
            name="name"
            required
            [placeholder]="ts.t().contact.form.namePlaceholder"
            class="w-full px-4 py-3 rounded-xl border border-slate-200 focus:border-brand-orange focus:ring-2 focus:ring-brand-orange/20 outline-none transition-all"
            [disabled]="isSubmitting()"
          >
        </div>

        <div class="mb-4">
          <input 
            type="email" 
            [(ngModel)]="formData.email" 
            name="email"
            required
            [placeholder]="ts.t().contact.form.emailPlaceholder"
            class="w-full px-4 py-3 rounded-xl border border-slate-200 focus:border-brand-orange focus:ring-2 focus:ring-brand-orange/20 outline-none transition-all"
            [disabled]="isSubmitting()"
          >
        </div>

        <div class="mb-6">
          <textarea 
            [(ngModel)]="formData.message" 
            name="message"
            required
            rows="4"
            [placeholder]="ts.t().contact.form.messagePlaceholder"
            class="w-full px-4 py-3 rounded-xl border border-slate-200 focus:border-brand-orange focus:ring-2 focus:ring-brand-orange/20 outline-none transition-all resize-none"
            [disabled]="isSubmitting()"
          ></textarea>
        </div>

        <button 
          type="submit" 
          [disabled]="isSubmitting() || !contactForm.form.valid"
          class="w-full py-3 bg-brand-orange text-white font-bold rounded-xl hover:bg-[#c5662a] transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex justify-center items-center"
        >
          @if (isSubmitting()) {
            <span class="material-icons-round animate-spin mr-2">refresh</span>
            Відправка...
          } @else {
            {{ ts.t().contact.form.submit }}
          }
        </button>

        @if (status() === 'success') {
          <div class="mt-4 p-3 bg-green-100 text-green-700 rounded-lg text-center">
            ✅ {{ ts.t().contact.form.success }}
          </div>
        }
        @if (status() === 'error') {
          <div class="mt-4 p-3 bg-red-100 text-red-700 rounded-lg text-center">
            ❌ Сталася помилка. Спробуйте пізніше.
          </div>
        }
      </form>
    </div>
  `
})
export class ContactComponent {
  ts = inject(TranslationService);
  appwrite = inject(AppwriteService);

  // Об'єкт для даних форми
  formData = {
    name: '',
    email: '',
    message: ''
  };

  isSubmitting = signal(false);
  status = signal<'idle' | 'success' | 'error'>('idle');

  async onSubmit() {
    if (!this.formData.name || !this.formData.email || !this.formData.message) return;

    this.isSubmitting.set(true);
    this.status.set('idle');

    try {
      await this.appwrite.sendRequest(this.formData);

      // Успіх!
      this.status.set('success');
      this.formData = { name: '', email: '', message: '' }; // Очищаємо форму

      // Ховаємо повідомлення про успіх через 5 секунд
      setTimeout(() => this.status.set('idle'), 5000);

    } catch (error) {
      this.status.set('error');
    } finally {
      this.isSubmitting.set(false);
    }
  }
}
