import { Component, ChangeDetectionStrategy, inject, computed } from '@angular/core';
import { TranslationService } from '../services/translation.service';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-projects',
  standalone: true,
  imports: [CommonModule],
  template: `
    <section id="projects" class="py-20 bg-brand-cream">
      <div class="max-w-7xl mx-auto px-4">
        <h2 class="text-3xl font-bold text-center mb-10 text-brand-dark">{{ ts.t().projects.title }}</h2>
        <div class="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div class="bg-white p-6 rounded-2xl shadow-sm border-t-4 border-brand-orange">
             <h3 class="text-xl font-bold mb-4 text-brand-dark">{{ ts.t().projects.donors }}</h3>
             <div *ngFor="let item of donorProjects()" class="mb-4 p-4 bg-slate-50 rounded border border-slate-100">
               <strong class="block text-lg mb-1">{{ item.title }}</strong>
               <span class="text-slate-600 text-sm">{{ item.desc }}</span>
             </div>
          </div>
          <div class="bg-white p-6 rounded-2xl shadow-sm border-t-4 border-brand-yellow">
             <h3 class="text-xl font-bold mb-4 text-brand-dark">{{ ts.t().projects.probono }}</h3>
             <div *ngFor="let item of probonoProjects()" class="mb-4 p-4 bg-slate-50 rounded border border-slate-100">
               <strong class="block text-lg mb-1">{{ item.title }}</strong>
               <span class="text-slate-600 text-sm">{{ item.desc }}</span>
             </div>
          </div>
        </div>
      </div>
    </section>
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProjectsComponent {
  ts = inject(TranslationService);

  // Явно вказуємо типи для фільтрації
  donorProjects = computed(() => this.ts.t().projects.items.filter((p: any) => p.type === 'donor'));
  probonoProjects = computed(() => this.ts.t().projects.items.filter((p: any) => p.type === 'probono'));
}
