import { Component, ChangeDetectionStrategy, inject } from '@angular/core';
import { TranslationService } from '../services/translation.service';
import { CommonModule } from '@angular/common';
import { GlowComponent } from './glow.component'; // <-- Імпортуємо Glow

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [CommonModule, GlowComponent], // <-- Додаємо в imports
  template: `
    <section id="home" class="relative pt-32 pb-40 md:pt-48 md:pb-56 overflow-hidden bg-[#2E1A12] text-white">

      <div class="absolute inset-0 overflow-hidden pointer-events-none">
        <app-glow variant="top" className="-top-[100px] opacity-100"></app-glow>
      </div>

      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center relative z-10 animate-appear-zoom">

        <div class="inline-flex items-center px-4 py-1.5 rounded-full bg-brand/10 border border-brand/20 backdrop-blur-md text-brand-foreground text-sm font-medium mb-8">
          <span class="material-icons-round text-base mr-2">verified</span>
          {{ ts.t().hero.badge }}
        </div>

        <h1 class="text-4xl md:text-7xl font-extrabold tracking-tight mb-6 leading-tight drop-shadow-2xl">
          {{ ts.t().hero.titleStart }} <br class="hidden md:block"/>
          <span class="text-transparent bg-clip-text bg-gradient-to-r from-brand-foreground to-brand">
            {{ ts.t().hero.titleEnd }}
          </span>
        </h1>

        <p class="mt-6 max-w-2xl mx-auto text-xl text-white/70 mb-10 font-light leading-relaxed">
          {{ ts.t().hero.description }}
        </p>

        <div class="flex flex-col sm:flex-row justify-center gap-4">
          <button (click)="scrollTo('contact')" class="px-8 py-4 bg-brand text-white font-bold rounded-md hover:bg-brand/90 transition-all shadow-[0_0_30px_-10px_hsl(var(--brand))]">
            {{ ts.t().hero.btnContact }}
          </button>

          <button (click)="scrollTo('about')" class="px-8 py-4 bg-transparent border border-white/20 text-white font-bold rounded-md hover:bg-white/10 transition-all">
            {{ ts.t().hero.btnMore }}
          </button>
        </div>
      </div>
    </section>
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class HeroComponent {
  ts = inject(TranslationService);
  scrollTo(id: string) { document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' }); }
}
